$(function() {
	//需要参数  localhost() 
	var preUrl = window.location.href;
	var splitUrl = getUrl(preUrl);
//	console.log(splitUrl)
	var id = splitUrl.id;
	var ddd=splitUrl.type;

//	var problemId = splitUrl.id;
	var title = decodeURI(splitUrl.acceptionTitle);
	$(".wall>p").html(title);
	var shuju = {
			"id": id,
		"problemId": id,
		"acceptionTitle":title,
		"type":1
		//"acceptionAudioPath": acceptionAudioPath,
	};
	
	
	$(".header-right").on("touchstart", function() {
		var acceptionContent = $(".wall textarea").val();
		shuju.acceptionContent = acceptionContent;
		$.ajax({
			url: localhost() + "answer/add_answer",
			dataType: "json",
			contentType: "application/json",
			async: true,
			data: JSON.stringify(shuju),
			type: "POST",
			beforeSend: function() {
				console.log("======== 请求之前调用 =========")
			},
			success: function(data) {
		if(shuju.code==1002){window.location.href=locationHref()+'yishengjiaoyu/login';}

				console.log("========= 服务器响应成功调用 ========== ");
				console.log(data);
				if(data.data == 1) {
					var urls = "yishengjiaoyu/queDetail.html?id=" + shuju.id+"&timestamp="+timestamp+"&type=1";
					getPath(urls);
				}
			},
			error: function() {
				console.log("======= 服务器响应失败调用 ========== ")
			},
			complete: function() {
				console.log("======== 请求完毕之后调用，注意：无论服务器响应成功与否，都会调用这个函数！！ === === === == ")
			}
		})

	});
	
	$(".header-left").on("touchstart",function() {
		if (ddd==0){
			var urls = "yishengjiaoyu/html/qzindex.html";
			getPath(urls);
		} else if (ddd==1){
			window.history.go(-1); 
		}
		
	});

});