$(function() {
			//需要参数  localhost() 
			var preUrl = window.location.href; //window.location.href
			var splitUrl = getUrl(preUrl);
			console.log(splitUrl)
			var type = splitUrl.type;
			var id = splitUrl.id;
			console.log(id + " " + type)
			$.ajax({
				url: localhost() + "problem/get_details?type=" + type + "&id=" + id,
				dataType: "json",
				async: true,
				type: "GET",
				beforeSend: function() {
					console.log("======== 请求之前调用 =========")
				},
				success: function(shuju) {
			getLogin(shuju.code)
					console.log("========= 服务器响应成功调用 ==========");
					$(".section1 img").attr("src", shuju.data.userModel.userUrl);
					$(".section1 span").html(shuju.data.userModel.userName);
					$(".section1 p").html(shuju.data.problemTitle);
					$(".section2-div1 img").attr("src", shuju.data.userUrl);
					$(".section2-div1 span:nth-of-type(2)").html(shuju.data.acceptionAudios);
					$(".section2-div2 span:nth-of-type(1)").html(shuju.data.userName);
					$(".section2-div2 span:nth-of-type(2)").html(shuju.data.specialistJobtitle);
					$(".section2-div3 span").html(shuju.data.likeTotal + "赞过");

					function jsDateDiff(publishTime) {
						var d_minutes, d_hours, d_days;
						var timeNow = Date.parse(new Date()) / 1000;
						console.log(timeNow);
						var d;
						var s = new Date(publishTime * 1000);
						d = timeNow - publishTime;
						d_days = parseInt(d / 86400);
						d_hours = parseInt(d / 3600);
						d_minutes = parseInt(d / 60);
						if(d_days > 0 && d_days < 4) {
							return d_days + "天前"
						} else if(d_days <= 0 && d_hours > 0) {
							return d_hours + "小时前"
						} else if(d_hours <= 0 && d_minutes > 0) {
							return d_minutes + "分钟前"
						} else if(d_minutes <= 0) {
							return "刚刚"
						} else {
							return s.getFullYear() + "年" + (s.getMonth() + 1) + "月" + s.getDate() + "日";
						}
					}
					$(".section2-div2 span:nth-of-type(5)").html(jsDateDiff(shuju.data.time));
					$(".content-name").html(shuju.data.userName);

					$(".wall #myAudio source").attr("src",shuju.data.acceptionAudioPath)
//					console.log(shuju.data.acceptionAudioPath)
					$(".wall .section2-div1").attr("id", shuju.data.id)
					$(function() {
						$(".voice1").on("touchstart", function() {
							var id2 = $(".wall .section2-div1").attr("id")
							console.log(shuju.data.orderStatus)
							if(shuju.data.orderStatus == 0) {
								var Audio = this.getElementsByTagName("audio")[0];
								if(Audio.paused) {
									Audio.load()
									Audio.play()
								} else {
									Audio.pause()
								}
							} else {
								window.location.href = "http://yishengjiaoyu/order.html?id=" + id2 + "&url="+locationHref()+"yishengjiaoyu/wendaDetail.html?id=" + id
							}

						})
					});
					if(shuju.data.userSex) {
						var img1 = document.createElement("img");
						img1.src = "./img/ic_man.png";
						$(".content-sex").append(img1)
					} else {
						var img1 = document.createElement("img");
						img1.src = "./img/sex-w.png";
						$(".content-sex").append(img1)
					}
					if(shuju.data.teacherType == "教") {
						if(shuju.data.userType == "专") {
							$("<img src='img/ic_zhuan_jiao.png'>").appendTo(".content-img")
						} else {
							$("<img src='img/ic_jiao.png'>").appendTo(".content-img")
						}
					} else {
						if(shuju.data.userType == "专") {
							$("<img src='img/ic_zhuan.png'>").appendTo(".content-img")
						} else {
							$(".content-img").hide()
						}
					}
					$(".content-zhiwu").html(shuju.data.specialistJobtitle);
					$(".section3-div1 p").html(shuju.data.problemContent);
					for(var value of shuju.data.relatedAnswerList) {
						$("<li><p>" + value.problemTitle + "</p><p><span class=''>" + value.userName + "</span><span class=''>" + value.specialistJobtitle + " </span><span class=''></span><span class=''>" + value.orderTotal + "人听过</span></p></li>").appendTo(".section4 ul")
					}
				},
				error: function() {
					console.log("======= 服务器响应失败调用 ==========")
				},
				complete: function() {
					console.log("======== 请求完毕之后调用，注意：无论服务器响应成功与否，都会调用这个函数！！ ===========")
				}
			});
			//var urls=需要跳转到的页面
			$(".header-left").on("touchstart", function() {
				var urls = 'yishengjiaoyu/html/qzindex.html';
				getPath(urls);
			});
	});